echo "Counting `(cd /data/ftp/customer; \ls */licenses/*cmdr*.txt | grep -v eval | wc -l)` commander customer licenses & `(cd /data/ftp/customer; \ls */licenses/*cmdr*.txt | grep eval | wc -l)` commander eval licenses"
echo "Customer list of Commander Concurrent user licenses:"; echo "`grep -A1 maxConcurrentUsers /data/ftp/customer/*/licenses/*-cmdr-*.txt | egrep -v '\-\-' | grep -v eval | grep -v maxConcurrentUsers  | grep -v 'Value>0' | grep -v 'Value>999999' | sed s/'\/'/'\<'/g | awk -F'<' '{print $8 " " $5}' | sed s/'propertyValue>'/' '/g | sort -n | tee /dev/tty | wc -l` Total Customers "
echo "Commander named user licenses:"; echo "`grep -A1 maxHosts /data/ftp/customer/*/licenses/*-cmdr-*.txt | egrep -v '\-\-' | grep -v eval | grep -v maxNamedUser | grep -v 'Value>0' | sed s/'\/'/'\<'/g | awk -F'<' '{print $8 " " $5}' | sed s/'propertyValue>'/' '/g | sort -n | tee /dev/tty | wc -l` Customers "
echo "Commander host licenses:"; echo "`grep -A1 maxHosts /data/ftp/customer/*/licenses/*-cmdr-*.txt | egrep -v '\-\-' | grep -v eval | grep -v maxHosts | grep -v 'Value>0' | sed s/'\/'/'\<'/g | awk -F'<' '{print $8 " " $5}' | sed s/'propertyValue>'/' '/g | sort -n | tee /dev/tty | wc -l` Customers "
echo "Commander Proxy host licenses:"; echo "`grep -A1 maxHosts /data/ftp/customer/*/licenses/*-cmdr-*.txt | egrep -v '\-\-' | grep -v eval | grep -v maxProxyHosts | grep -v 'Value>0' | sed s/'\/'/'\<'/g | awk -F'<' '{print $8 " " $5}' | sed s/'propertyValue>'/' '/g | sort -n | tee /dev/tty | wc -l` Customers "
echo "Commander Patches `(cd /data/ftp/customer; \ls */*NMB* | awk -F'/' '{print $2}' | sort -u | tee /dev/tty | wc -l)`"
